function getTooltip(dataTable, rowNum) {
  const dateObject = new Date(dataTable.getValue(rowNum, 0));
  const formattedDate = new Intl.DateTimeFormat('en-US', {
    year: 'numeric',
    month: 'short',
    day: '2-digit',
  }).format(dateObject)

  return `
  <b>${formattedDate}</b>
  <br/>
  ${dataTable.getColumnLabel(1)}: 
  <b>${dataTable.getValue(rowNum, 1)}</b>
  (${dataTable.getValue(rowNum, 2)})
  `
}
